//the default user to update and delete is {user_Id:1,username:'testuser',email:'test@test.com',password:'password}
import "./App.css";
import Home from "./components/Home.jsx";
import AllRecepies from "./components/AllRecipies.jsx"
import { useEffect, useState } from "react";
// import OneRecipe from "./components/oneRecipe.jsx";
// import Add from "./components/Add.jsx";
import dummyData from "./data/data.json";
import axios from "axios";

function App() {
const [view,setView]=useState('Home')
const [data, setData] = useState(dummyData);
  const [first, setFirst] = useState(data[0]);
  const [search, setSearch] = useState("");
  useEffect(() => {
    axios.get("http://localhost:4000/recipe").then((res) => {
      setData(res.data);
    });
  }, []);


  let changeView = (view) => {
    setView(view);
  };
  return (
    <div className="App">
      <nav className="nav">
        <div
          className="nav-item is-active"
          active-color="orange"
          onClick={() => setView("Home")}
        >
          Home
        </div>

        <div
          className="nav-item"
          active-color="green"
          onClick={() => setView("Allrecepies")}
        >
          All Recipies
        </div>
        <div
          className="nav-item"
          active-color="red"
          onClick={() => setView("Addrecepie")}
        >
          Addrecepie
        </div>
        <div className="nav-item" active-color="red">
          <input type="text"  />
          <button  onClick={() => {
              setData(
                data.filter((e) =>
                  e.Recipe_Name.toLowerCase().includes(search.toLowerCase())
                )
              );
            }}>search</button>
        </div>
        <span className="nav-indicator"></span>
      </nav>
      {view === "Home" && <Home changeView={changeView}/>}
      {view === "Allrecepies" && <AllRecepies  changeView={changeView} setFirst={setFirst} data={data}/>}
      {/* {view === "AddRecipe" && <Add one={first} />}
      {view === "OneRecipe" && <OneRecipe one={first} />} */}
      <div></div>
    </div>
  );
}

export default App;
