import React from "react";
import "../App.css"
import "../index.scss";


const OneRecipe = ({ one }) => {
  return (
    <div>
      <div className="receipe-content-area">
        <div className="container">
          <div className="header">
            <img width="500" height="600" src={one.receipe_Image} alt="" />
            <div className="icon">
              <a href="/">
                <i className="fa fa-heart-o" />
              </a>
            </div>
          </div>
          <div className="text">
            <h1 className="food">{one.receipe_Name}</h1>
            <i className="fa fa-clock-o"> {one.Cook_Time} Mins</i>
            <i className="fa fa-users"> Serves {one.Serves}</i>

            <p className="info">{one.receipe_Description}</p>
          </div>
        </div>
      </div>
      
    </div>
  );
};

export default OneRecipe;
